import "jsr:@supabase/functions-js/edge-runtime.d.ts";

interface NotificationRequest {
  userId: string;
  title: string;
  body: string;
  data?: Record<string, any>;
}

Deno.serve(async (req: Request) => {
  try {
    // Only allow POST requests
    if (req.method !== 'POST') {
      return new Response(
        JSON.stringify({ error: 'Method not allowed' }),
        { status: 405, headers: { 'Content-Type': 'application/json' } }
      );
    }

    const { userId, title, body, data }: NotificationRequest = await req.json();

    // Validate required fields
    if (!userId || !title || !body) {
      return new Response(
        JSON.stringify({ error: 'Missing required fields: userId, title, body' }),
        { status: 400, headers: { 'Content-Type': 'application/json' } }
      );
    }

    // In a real implementation, you would:
    // 1. Get the user's push notification tokens from the database
    // 2. Send push notifications using a service like Firebase Cloud Messaging
    // 3. Store the notification in the database for in-app display

    console.log('Sending notification:', { userId, title, body, data });

    // For now, just return success
    return new Response(
      JSON.stringify({ 
        success: true, 
        message: 'Notification sent successfully',
        userId,
        title,
        body
      }),
      { 
        status: 200, 
        headers: { 
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*',
          'Access-Control-Allow-Methods': 'POST, OPTIONS',
          'Access-Control-Allow-Headers': 'Content-Type, Authorization'
        } 
      }
    );
  } catch (error) {
    console.error('Error sending notification:', error);
    return new Response(
      JSON.stringify({ error: 'Internal server error' }),
      { status: 500, headers: { 'Content-Type': 'application/json' } }
    );
  }
});

import "jsr:@supabase/functions-js/edge-runtime.d.ts";

interface ModerationRequest {
  content: string;
  type: 'post' | 'comment' | 'message';
  userId: string;
}

interface ModerationResult {
  isAppropriate: boolean;
  confidence: number;
  reasons?: string[];
  suggestedAction: 'approve' | 'flag' | 'reject';
}

// Simple content moderation rules
const INAPPROPRIATE_WORDS = [
  'spam', 'scam', 'hate', 'abuse', 'harassment'
];

const SUSPICIOUS_PATTERNS = [
  /\b\d{4}[\s-]?\d{4}[\s-]?\d{4}[\s-]?\d{4}\b/, // Credit card numbers
  /\b\d{3}[\s-]?\d{2}[\s-]?\d{4}\b/, // SSN pattern
  /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}\b/g, // Email addresses
];

function moderateContent(content: string): ModerationResult {
  const lowerContent = content.toLowerCase();
  const reasons: string[] = [];
  let confidence = 1.0;

  // Check for inappropriate words
  const foundInappropriateWords = INAPPROPRIATE_WORDS.filter(word => 
    lowerContent.includes(word)
  );

  if (foundInappropriateWords.length > 0) {
    reasons.push(`Contains inappropriate words: ${foundInappropriateWords.join(', ')}`);
    confidence = 0.9;
  }

  // Check for suspicious patterns
  const foundSuspiciousPatterns = SUSPICIOUS_PATTERNS.filter(pattern => 
    pattern.test(content)
  );

  if (foundSuspiciousPatterns.length > 0) {
    reasons.push('Contains suspicious patterns (potential PII or spam)');
    confidence = Math.min(confidence, 0.8);
  }

  // Check content length (very short or very long might be spam)
  if (content.trim().length < 3) {
    reasons.push('Content too short');
    confidence = Math.min(confidence, 0.7);
  }

  if (content.length > 2000) {
    reasons.push('Content unusually long');
    confidence = Math.min(confidence, 0.8);
  }

  // Determine action based on findings
  let suggestedAction: 'approve' | 'flag' | 'reject' = 'approve';
  let isAppropriate = true;

  if (reasons.length > 0) {
    if (confidence < 0.7) {
      suggestedAction = 'reject';
      isAppropriate = false;
    } else if (confidence < 0.9) {
      suggestedAction = 'flag';
      isAppropriate = true; // Allow but flag for review
    }
  }

  return {
    isAppropriate,
    confidence,
    reasons: reasons.length > 0 ? reasons : undefined,
    suggestedAction
  };
}

Deno.serve(async (req: Request) => {
  try {
    // Handle CORS preflight
    if (req.method === 'OPTIONS') {
      return new Response(null, {
        status: 200,
        headers: {
          'Access-Control-Allow-Origin': '*',
          'Access-Control-Allow-Methods': 'POST, OPTIONS',
          'Access-Control-Allow-Headers': 'Content-Type, Authorization'
        }
      });
    }

    // Only allow POST requests
    if (req.method !== 'POST') {
      return new Response(
        JSON.stringify({ error: 'Method not allowed' }),
        { status: 405, headers: { 'Content-Type': 'application/json' } }
      );
    }

    const { content, type, userId }: ModerationRequest = await req.json();

    // Validate required fields
    if (!content || !type || !userId) {
      return new Response(
        JSON.stringify({ error: 'Missing required fields: content, type, userId' }),
        { status: 400, headers: { 'Content-Type': 'application/json' } }
      );
    }

    // Moderate the content
    const moderationResult = moderateContent(content);

    console.log('Content moderation result:', {
      userId,
      type,
      contentLength: content.length,
      result: moderationResult
    });

    return new Response(
      JSON.stringify({
        success: true,
        moderation: moderationResult,
        timestamp: new Date().toISOString()
      }),
      { 
        status: 200, 
        headers: { 
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*',
          'Access-Control-Allow-Methods': 'POST, OPTIONS',
          'Access-Control-Allow-Headers': 'Content-Type, Authorization'
        } 
      }
    );
  } catch (error) {
    console.error('Error moderating content:', error);
    return new Response(
