-- Create a function to prevent duplicate profiles
CREATE OR REPLACE FUNCTION prevent_duplicate_profiles()
RETURNS TRIGGER AS $$
BEGIN
  -- Check if a profile already exists for this user_id
  IF EXISTS (SELECT 1 FROM profiles WHERE user_id = NEW.user_id AND id != COALESCE(NEW.id, '00000000-0000-0000-0000-000000000000'::uuid)) THEN
    RAISE EXCEPTION 'A profile already exists for this user';
  END IF;

  RETURN NEW;
END;
$$ LANGUAGE plpgsql;

-- Create trigger to run before insert or update
DROP TRIGGER IF EXISTS prevent_duplicate_profiles_trigger ON profiles;
CREATE TRIGGER prevent_duplicate_profiles_trigger
  BEFORE INSERT OR UPDATE ON profiles
  FOR EACH ROW
  EXECUTE FUNCTION prevent_duplicate_profiles();

