import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'jsr:@supabase/supabase-js@2';

const supabaseUrl = Deno.env.get('SUPABASE_URL')!;
const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!;

const supabase = createClient(supabaseUrl, supabaseServiceKey);

Deno.serve(async (req: Request) => {
  try {
    if (req.method !== 'POST') {
      return new Response('Method not allowed', { status: 405 });
    }

    // Clean up unconfirmed users older than 24 hours
    const { data: unconfirmedUsers, error: fetchError } = await supabase.auth.admin.listUsers({
      page: 1,
      perPage: 1000
    });

    if (fetchError) {
      throw fetchError;
    }

    const now = new Date();
    const oneDayAgo = new Date(now.getTime() - 24 * 60 * 60 * 1000);

    let cleanedCount = 0;

    for (const user of unconfirmedUsers.users) {
      // If user is not confirmed and was created more than 24 hours ago
      if (!user.email_confirmed_at && new Date(user.created_at) < oneDayAgo) {
        try {
          await supabase.auth.admin.deleteUser(user.id);
          cleanedCount++;
          console.log(`Deleted unconfirmed user: ${user.email}`);
        } catch (deleteError) {
          console.error(`Failed to delete user ${user.email}:`, deleteError);
        }
      }
    }

    return new Response(
      JSON.stringify({
        success: true,
        message: `Cleaned up ${cleanedCount} unconfirmed users`,
        cleanedCount
      }),
      {
        headers: {
          'Content-Type': 'application/json',
        },
      }
    );
  } catch (error) {
    console.error('Cleanup error:', error);
    return new Response(
      JSON.stringify({
        success: false,
        error: error.message
      }),
      {
        status: 500,
        headers: {
          'Content-Type': 'application/json',
        },
      }
    );
  }
});

